Queries.Template:
{
    "query": {
        "bool": {
            "must": [
                {
                    "term": { "Content.ContentItem.ContentType": "Department" }
                },
                {
                    "term": { "Content.ContentItem.Published": 'true' }
                },
                {
                    "term": { "Content.ContentItem.Latest": "true" }
                }
            ],
            {% if filters %}
            "filter":[
                  {% for filter in filters %} 
                	{"{{filter.method}}": { "{{filter.key}}": "{{filter.value}}" }},
				  {% endfor %} 
            	]
            {% endif %}
        }
    },
    "from":0,
  	"size":10000
}

{
    "query": {
        "bool": {
            "must": [
                { "term": { "Content.ContentItem.ContentType": "Customer" } },
                { "term": { "Content.ContentItem.Published": 'true' } },
                {  "term": { "Content.ContentItem.Latest": "true" } }
            ],
            {% if filters %}
            "filter":[
                  {% for filter in filters %} 
              		{%if filter.key %}
      					{"{{filter.method}}": { "{{filter.key}}": "{{filter.value}}" }},
                    {%else%}
                    	{"{{filter.method}}":"{{filter.value}}"}},
                    {%endif%}
				  {% endfor %} 
            	]
            {% endif %}
        }
    }
  {% if from %}
    ,"from":{{from}}
  {% endif %}
  {%if size %}
   ,"size":{{size}}
  {%endif%}  	
}
